import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import '../App.css'

const Display = () => {
  let params = useParams()
  const [target, setTarget] = useState({
    name: params.name,
    target: '',
    assassin: '',
    alive: true
  })

  const killTarget = () => {
    // instead of this, make call to server to get the current target after kill
    let incName = `New ${target}`
    setTarget({
      name: incName,
      target: '',
      assassin: '',
      alive: true
    })
  }

  return (
    <div className='TargetDisplay'>
      <div >
        <h1 className='displayText' > {"Target"} </h1>
      </div>
      <br></br>
      <button className='KillButton' onClick={killTarget}>
        <h1 className='displayText'> {target.name} </h1>
      </button>

    </div>
  )
}

export default Display;
