import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import '../App.css'

const Display = () => {
  let params = useParams()
  let playerResponse = {}
  axios
    .get('http://localhost:8000/survive', { name: params.name })
    .then((res) => {
      playerResponse = {
        name: res.data.name,
        target: res.data.target,
        assassin: res.data.assassin,
        alive: res.data.alive
      }
    })
    .catch((err, res) => {
      res.send(`Error: ${err}`)
    })
  const [player, setPlayer] = useState({ playerResponse })

  const killTarget = () => {
    axios
      .patch('http://localhost:8000/survive/kill', { name: player.target })
      .then((res) => {
        setPlayer({
          name: res.data.name,
          target: res.data.target,
          assassin: res.data.assassin,
          alive: res.data.alive
        })
      })
      .catch((err, res) => {
        res.send(`Error: ${err}`)
      })
  }

  return (
    <div className='TargetDisplay'>
      <div >
        <h1 className='displayText' > {"Target"} </h1>
      </div>
      <br></br>
      <button className='KillButton' onClick={killTarget}>
        <h1 className='displayText'> {player.target} </h1>
      </button>

    </div>
  )
}

export default Display;
