import React, { useState } from 'react'
import '../App.css'

const Display = () => {
  const [target, setTarget] = useState('Original Target')

  // const onChange = (newTarget) => {
  //   setTarget(newTarget)
  // }

  const killTarget = () => {
    setTarget("New Target")
  }

  return (
    <div className='TargetDisplay'>
      <div >
        <h1 className='displayText' > {"Target"} </h1>
      </div>
      <br></br>
      <button className='KillButton' onClick={killTarget}>
        <h1 className='displayText'> {target} </h1>
      </button>

    </div>
  )
}

export default Display;
